/**
 * Response returned to the client when not waiting for the results.
 */
export interface ApiResponseSimple {
    /**
     * The unique id of the request.
     *
     * The id of the ApiRequest that was created for this request.
     */
    id: string;
    /**
     * The status url of the request.
     *
     * The current JobStatus of this request.
     */
    statusUri: string;
    /**
     * Messages dispatched by the integration.
     */
    beaconUri: string;
    /**
     * The cost of the request in centi-credits.
     */
    cost: number;
    /**
     * The remaining balance in centi-credits.
     */
    balance: number;
    /**
     * Number of milliseconds it took to complete the request.
     */
    time: number;
}
/**
 * Response returned to the client when waiting for the results.
 */
export interface ApiResponse extends ApiResponseSimple {
    /**
     * The images generated during the ApiRequest.
     *
     * Only included in the response when the request contained only a single page to scrape.
     */
    images?: string[];
    /**
     * The pdfs generated during the ApiRequest.
     *
     * Only included in the response when the request contained only a single page to scrape.
     */
    pdfs?: string[];
    /**
     * Additional results generated by integrations.
     *
     * Integrations typically generate screenshots and pdfs, but they can also
     * generate additional data. For example, a crawler integration may return a
     * list of urls that it crawled.
     *
     * The type of the results is an array with each integration that generated results
     * being pushed onto the array. The order of the integrations is the same order
     * that the integrations were run.
     */
    results?: any[];
    /**
     * User supplied metadata that will be a part of the api status.
     *
     * Used by clients to pass along a unique value that they need to associate with the request. They
     * can then find this value in the status response.
     */
    meta?: string;
}
